// Check if Object prototype is altered
function isMyObjectPrototypeFucked() {
	var obj = {};
	for (var key in obj) {
		if (!obj.hasOwnProperty(key)) return true;
	}
	return false;
}

// Clean Object prototype
function cleanObjectPrototype() {
	var obj = {};
	for (var key in obj) {
		if (!obj.hasOwnProperty(key)) delete Object.prototype[key];
	}
}

